import React, { useState } from 'react';
import { useHistory, Link, useNavigate } from 'react-router-dom';
import Login from "./login";

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const [confirmPwdMsg, setConfirmPwdMsg] = useState('');

  const [register, setRegister] = useState(false);
  const [passwordValidation, setPasswordValidation] = useState('');
  const [message, setMessage] = useState('');

  const[successMsg, setSuccessMsg] = useS

  const[apiError, setApiError] = useState('');

  const handleUsernameChange = event => setUsername(event.target.value);
  const handleEmailChange = event => setEmail(event.target.value);
  const handlePasswordChange = event => setPassword(event.target.value);
  const handleConfirmPasswordChange = event => setConfirmPassword(event.target.value);

  const navigate = useNavigate();

  const handleSubmit = event => {
    event.preventDefault();

    const passwordRegex = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
    if (!passwordRegex.test(password)) {
        setPasswordValidation('Password must contain at least 8 characters including 1 uppercase, 1 lowercase, 1 digit and 1 special character');
        return;
    }

    if(!confirmPassword){
      setConfirmPwdMsg("Password donot match!Please confirm your password");
      return
    }
    const data = { username, email, password };

    fetch('http://localhost:8000/api/register/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data)
    })
      .then(response => {
        if (response.ok) {
          setRegister(true);
          setMessage("Successfully signed up! Please wait while you are redirected to the login page...");
          setTimeout(() => {
            navigate("/login");
          }, 1500); 
        }
      })
      .catch((error) => {
        console.error(error);
        setApiError('An error occurred while signing up. Please try again.');
      }

      );
  };


  // if(register){
  //   setMessage("Successfully signed up!Please wait you been redirecting to login page......");
  //   setTimeout(() => {
  //     setMessage("Successfully signed up!Please wait you been redirecting to login page......");
  //     navigate("/login");
  //   }, 1500); 
  // }



  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label><br></br>
          <input type="text" id="username" value={username} onChange={handleUsernameChange} />
        </div>
        <div>
          <label htmlFor="email">Email:</label><br></br>
          <input type="email" id="email" value={email} onChange={handleEmailChange} />
        </div>
        <div>
          <label htmlFor="password">Password:</label><br></br>
          {passwordValidation && <span>{passwordValidation}</span>} <br></br>
          <input type="password" id="password" value={password} onChange={handlePasswordChange} />
        </div>

        <div>
          <label htmlFor="password">Confirm Password:</label><br></br>
          {confirmPwdMsg && <span>{confirmPwdMsg}</span>} <br></br>
          <input type="password" id="confirmPassword" value={confirmPassword} onChange={handleConfirmPasswordChange} />
        </div>

        {apiError && <h4>{apiError}</h4>}
        {message && <h4>{message}</h4>}


        <div>
        <button type="submit">Signup</button>
        <h5>Already Have an Account?</h5>
        <Link to="/login">Login</Link>
          </div>
      </form>
    </div>
  );
};

export default Register;
