import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';

export default function Likes({ blogId, likes, likedBy, authorId }) {
  const [isLiked, setIsLiked] = useState(likedBy.includes(authorId));
  const [numLikes, setNumLikes] = useState(likes);
  const [likedByList, setLikedByList] = useState(likedBy);
  const [error, setError] = useState("");
  const [blogs, setBlogs] = useState()

  useEffect(() => {
    setIsLiked(likedBy.includes(authorId));
  }, [likedBy, authorId]);

  const handleLikeClick = async () => {
    try {
      setNumLikes((prevNumLikes) => {
        let newLikes = prevNumLikes;
        let newLikedByState = likedByList;
        let newLikedState = !isLiked;

        if (newLikedState) {
          newLikes += 1;
          newLikedByState = [...likedByList, authorId];
        } else {
          newLikes -= 1;
          newLikedByState = likedByList.filter((id) => id !== authorId);
        }

        // Check if unliking a post that was already liked by the user
        if (!newLikedState && likedByList.includes(authorId)) {
          newLikes -= 1;
        }

        setIsLiked(newLikedState);
        setLikedByList(newLikedByState);

        // Call API to update like state
        fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            liked: newLikedState,
            likes: newLikes,
            liked_by: newLikedByState,
          }),
        });

        return newLikes;
      });
    } catch (error) {
      console.error(error);
      setError("Failed to update like state.");
    }
  };

  return (
    <div>
       <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id}>
            <h3>{blog.title}</h3>
            <p>{blog.content}</p>
            <p>
              Author: {blog.author.first_name} {blog.author.last_name}
            </p>
            <p> {blog.liked_by}</p>
            <p>Likes: {blog.likes}</p>
liked/by{blog.liked_by} <br></br>
author id{blog.author_id}

<IconButton>
  <FavoriteIcon color={blog.liked_by.includes(blog.author_id) ? "error" : "primary"} />
</IconButton>


          </div>
        ))}
      </div>
    </div>
    )}
