import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [liked, setLiked] = useState([]);
  const [likes, setLikes] = useState(0);
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const [authorId,setAuthorId] = useState(0);
  const [countLikes, setCountLikes] = useState(0);
  const [likedBy,setLikedBy] = useState(0);
  const [newAddLikes,setNewAddLikes] = useState(0);
  const [newSubLikes,setNewSubLikes] = useState(0);
  const [likedByAuthor, setLikedByAuthor] = useState(false);
  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
        setLikedBy(blogsWithLiked.liked_by);
        setCountLikes(blogsWithLiked.likes);
        setAuthorId(blogsWithLiked.author);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);
  const handleLikeClick = async (blogId) => {
    const blog = blogs.find((blog) => blog.id === blogId);
    const likedByCurrentUser = blog.liked_by.includes(authorId);
    const newLikedBy = likedByCurrentUser
      ? blog.liked_by.filter((id) => id !== authorId)
      : [...blog.liked_by, authorI];

    const response = await fetch(
      `http://localhost:8000/api/blog/${blogId}/`,
      {
        method: "PATCH",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ liked_by: newLikedBy }),
      }
    );
    const data = await response.json();

    const updatedBlogs = blogs.map((blog) => {
      if (blog.id === blogId) {
        return {
          ...blog,
          liked: !likedByCurrentUser,
          liked_by: newLikedBy,
          likes: likedByCurrentUser
            ? blog.likes - 1
            : blog.likes + 1,
        };
      } else {
        return blog;
      }
    });

    setBlogs(updatedBlogs);
  };

  return (
    <div>

<div className="blogs">
  {blogs &&
    blogs.map((blog) => (
      <div key={blog.id}>
        <h3>{blog.title}</h3>
        <p>{blog.content}</p>
        <p>Likes: {blog.likes}</p>
        <p>Liked by: {blog.liked_by.join(", ")}</p>
        <p>
          Author: {blog.author.first_name} {blog.author.last_name}
        </p>
        <p> {blog.liked_by}</p>
        <p>Likes: {blog.likes}</p>
        <p>liked by: {blog.liked_by} </p>
        <IconButton onClick={() => handleLikeClick(blog.id)}>
  <FavoriteIcon color={blog.liked ? "error" : "inherit"} />
  <span>{blog.likes}</span>
</IconButton>


      </div>
    ))}
</div>



    </div>
  );
}
