import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // added state for redirecting

  const handleUsernameChange = event => setUsername(event.target.value);
  const handlePasswordChange = event => setPassword(event.target.value);
  const [validateMsg,setValidateMsg] = useState('');
  const [apiError,setApiError] = useState('');
  const navigate = useNavigate();


  const handleSubmit = event => {
    event.preventDefault();
    fetch('http://localhost:8000/api/login/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      })
      .then(response => {
        if (response.ok) {
          return response.json()
        } else {
          setValidateMsg('Invalid Credentials');
          throw new Error('Invalid Credentials,Login Failed');
        }
      })
      .then(data => {
        // Save the refresh and access tokens in local storage or cookies
        localStorage.setItem('refreshToken', data.refresh)
        localStorage.setItem('accessToken', data.access)
        setLoggedIn(true);
        setValidateMsg('Valid Credentials');
        navigate("/blog");
      })
      .catch(error => {
        console.error(error);
        setApiError('Something went wrong!Login Failed');
        console.log(error.message);
      })
    }

  return (

    <form onSubmit={handleSubmit}>
     <h2>Login</h2>
      <div>
        <label htmlFor="username">Username:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={handleUsernameChange}
        />
      </div>
      <div>
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={handlePasswordChange}
        />
      </div>
      {validateMsg && <span></span>}
      {apiError && <span>{apiError}</span>}
      <button type="submit">Log in</button>

      <h4>Donot have an account? Create an account?</h4> 
      <Link to="/signup">Signup </Link>
    </form>
  );
}

export default Login;
