import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';


const Likes = () => {
  const [likes, setLikes] = useState(null);
  const [liked, setLiked] = useState(false);

  useEffect(() => {
    // retrieve likes from API
    const fetchLikes = async () => {
      const response = await fetch('http://localhost:8000/api/blog/likes/', {
        headers: {
          Authorization: 'Bearer <your_token_here>'
        }
      });
      const data = await response.json();
      setLikes(data.likes);
    }
    fetchLikes();
  }, []);

  const handleLike = async () => {
    // update likes via API
    const response = await fetch('http://localhost:8000/api/blog/likes/update/2/', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: 'Bearer <your_token_here>'
      },
      body: JSON.stringify({
        author_id: 1,
        username: 'dilli',
        likes: likes + 1,
        liked: true
      })
    });
    const data = await response.json();
    setLikes(data.likes);
    setLiked(true);
  }

  return (
    <div>
      <p>{likes} likes</p>
      <button onClick={handleLike} disabled={liked}>Like</button>
    </div>
  );
};

export default Likes;
