function Likes(props) {
  const handleLikeClick = (id) => {
    const blogPost = props.blogPosts.find((post) => post.id === id);
    const newLikedState = !blogPost.liked;
    blogPost.likes += newLikedState ? 1 : -1;
    blogPost.liked = newLikedState;

    const data = {
      author_id: blogPost.author_id,
      blog_name: blogPost.blog_name,
      likes: blogPost.likes,
      liked: newLikedState
    }

    fetch(`http://localhost:8000/api/blog/likes/update/${id}/`, {
      method: "PUT",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data)
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(response.statusText);
      }
      return response.json();
    })
    .then(data => console.log(data))
    .catch(error => console.error(error));
  };

  return (
    <div>
      {props.blogPosts.map((post) => (
        <div key={post.id}>
          <h2>{post.blog_name}</h2>
          <p>Author: {post.author}</p>
          <p>Likes: {post.likes}</p>
          <button onClick={() => handleLikeClick(post.id)}>
            {post.liked ? 'Unlike' : 'Like'}
          </button>
        </div>
      ))}
    </div>
  );
}

export default Likes;
