import React, {useEffect, useState} from "react";
import ApiListCall from "./apiCall";
import axios from "axios";

export default function Create(){

    const [blogs, setBlogs] = useState("");
    const [newBlogTitle, setNewBlogTitle] = useState('')
    const [newBlogContent, setNewBlogContent] = useState('')
    const [newBlogAuthorName, setNewBlogAuthorName] = useState('')
    const [newBlogImage, setNewBlogImage] = useState(null);
    const [selectedBlogId, setSelectedBlogId] = useState("");


  useEffect(()=>{
    axios.get('http://localhost:8000/api/blog/list/',{
      headers: {
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
      }).then((response)=>{
          console.log(response.data);
          setBlogs(response.data);
      }).catch((e)=>{
          console.log(e);
      })
  })

  const handleCreate = () => {
    const newBlog = {
      title: newBlogTitle,
      content: newBlogContent,
      authorName: newBlogAuthorName,
      image: newBlogImage // Add the image file to the new blog object
    };

    const formData = new FormData();
    formData.append("title", newBlog.title);
    formData.append("content", newBlog.content);
    formData.append("authorName", newBlog.authorName);
    formData.append("image", newBlog.image);

    fetch("http://localhost:8000/api/blog/create/", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
      },
      body: formData, // Use the form data instead of JSON.stringify
    })
      .then((response) => response.json())
      .then((data) => {
        setBlogs([...blogs, data]);
        setNewBlogTitle("");
        setNewBlogContent("");
        setNewBlogAuthorName("");
        setNewBlogImage(null); // Reset the image state after submitting the form
      })
    }

    return(
        <div>

        <h2>Create New Blog</h2>
        <label>
        Title:
        <input
        type="text"
        value={newBlogTitle}
        onChange={(event) => setNewBlogTitle(event.target.value)}
        />
        </label>
        <br />
        <label>
        Content:
        <textarea
        value={newBlogContent}
        onChange={(event) => setNewBlogContent(event.target.value)}
        />
        </label>
        <br />
        <label>
        Author Name:
        <input
        type="text"
        value={newBlogAuthorName}
        onChange={(event) => setNewBlogAuthorName(event.target.value)}
        />
        </label>

        <label htmlFor="newBlogImage">Image:</label>
        <input type="file" id="newBlogImage" name="newBlogImage" onChange={event => setNewBlogImage(event.target.files[0])} />


        <br />
        <button onClick={handleCreate}>Create Blog</button>


        <h1>Blog List</h1>

{blogs.sort((a,b) => b.id - a.id).map(blog => (
  <li key={blog.id}>
    <h2>{blog.title}</h2>
    <img src={`http://localhost:8000${blog.image}`} style={{ width: "450px", maxWidth: "100%"}}/>

    <p>{blog.content}</p>
    <p>Author: {blog.authorName}</p>
    {/* <p>Published Date: {blog.created_at}</p>
    <p>Updated Date: {blog.updated_at}</p> */}
    {/* {created_at && <p>Blog Created at Standard: {created_at}</p>} */}
    <button onClick={() => setSelectedBlogId(blog.id)}>Edit</button>
    {/* <button onClick={() => handleDelete(blog.id)}>Delete</button> */}
  </li>
))}

        </div>




    )
}
