    const fetchLikes = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/likes/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          const blogLikes = data.map((blog) => ({
            id: blog.id,
            likes: blog.likes,
            liked_state: blog.liked_state,
            title: blog.title,
            image: blog.image,
            content: blog.content,
            authorName: blog.authorName,
            created_at: blog.created_at,
            updated_at: blog.updated_at,
            total_comments: blog.total_comments,
          }));

          setBlogs(blogLikes);
          setLikes(blogLikes.map((blog) => blog.likes));
          setLiked(blogLikes.map((blog) => blog.liked_state));
          setMainLoading(false);
          setVideoLoading(false);
          console.log("blog likes with total comments ", blogLikes);
        } else {
          console.error("Failed to fetch blog likes");
        }
      } catch (error) {
        console.error(error);
        setMainLoading(false);
      }
    };

    fetchLikes();
  }, []);

  useEffect(() => {
    fetchComments();
  }, []);

  const fetchComments = async () => {
    try {
      const response = await fetch("http://localhost:8000/api/blog/comments/", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
        },
      });

      if (response.ok) {
        const data = await response.json();
        console.log("comments are ", data);
        setComments(data);
      } else {
        console.error("Failed to fetch comments");
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  const handleLike = async (blogId, currentLikedState, index) => {
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${blogId}/`,
        {
          method: "PUT",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
          body: JSON.stringify({ liked: !currentLikedState }),
        }
      );

      if (response.ok) {
        const updatedBlogLikes = [...blogs];
        updatedBlogLikes[index].liked_state = !currentLikedState;
        if (currentLikedState) {
          updatedBlogLikes[index].likes--;
        } else {
          updatedBlogLikes[index].likes++;
        }
        setBlogs(updatedBlogLikes);

        const updatedLikes = [...likes];
        if (currentLikedState) {
          updatedLikes[index]--;
        } else {
          updatedLikes[index]++;
        }
        setLikes(updatedLikes);

        const updatedLiked = [...liked];
        updatedLiked[index] = !currentLikedState;
        setLiked(updatedLiked);
      }
    } catch (error) {
        console.error(error);
      }
    };

    const playVideo = (videoId) => {
      const video = document.getElementById(videoId);
      video.play();
    };

    const pauseVideo = (videoId) => {
      const video = document.getElementById(videoId);
      video.pause();
    };

    const handleVideoLoad = () => {
      setVideoLoading(false);

    };

    return (
      <div>
        <div>
          {mainLoading ? (
            <p>Loading...</p>
          ) : (
            blogs
              .sort((a, b) => b.id - a.id)
              .map((blog, index) => (
                <div key={blog.id}>
                  <h2>{blog.title}</h2>
                  <h3>Blog Id: {blog.id}</h3>

                  <div style={{ width: "100%", height: "100%" }}>
                    <img
                      src={`http://localhost:8000${blog.image}`}
                      style={{ width: "100%", maxWidth: "100%" }}
                      alt="Blog Image"
                    />
                  </div>
                  <br />

                  {blog.video ? (
                    <div>
                      <video
                        controls
                        style={{ maxWidth: "100%" }}
                        onLoadedData={handleVideoLoad}
                        onLoadStart={() => setVideoLoading(true)}
                        onError={() => console.log("Video loading error")}
                      >
                        <source
                          src={`http://localhost:8000${blog.video}`}
                          type="video/mp4"
                        />
                        Your browser does not support the video tag.
                      </video>
                      {videoLoading && <p>Loading...</p>}
                    </div>
                  ) : (
                    <p>Loading...</p>
                  )}

                  <div></div>

                  <div style={{ height: "200px", overflow: "hidden" }}>
                    <p>{blog.content}</p>
                    <p>Author: {blog.authorName}</p>
                    <p>Published Date: {blog.created_at}</p>
                    <p>Updated Date: {blog.updated_at}</p>
                    <br />
                  </div>
                </div>
              ))
          )}
        </div>
      </div>
    );

                  }
