import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';
import ShareIcon from '@mui/icons-material/Share';


import React, { useState, useEffect } from "react";

function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [userId, setUserId] = useState(null);

  useEffect(() => {
    // Fetch userId from API
    fetch("http://localhost:8000/api/user/")
      .then((response) => response.json())
      .then((data) => {
        setUserId(data.userId);
      })
      .catch((error) => console.log(error));
  }, []);

  useEffect(() => {
    // Fetch blogs from API
    fetch("http://localhost:8000/api/blog/list/")
      .then((response) => response.json())
      .then((data) => {
        setBlogs(data);
      })
      .catch((error) => console.log(error));
  }, []);

  const handleLikeClick = (blogId) => {
    const blogToUpdate = blogs.find((blog) => blog.id === blogId);

    if (blogToUpdate.liked_by.includes(userId)) {
      // User has already liked the blog, so remove like
      blogToUpdate.liked_by = blogToUpdate.liked_by.filter(
        (id) => id !== userId
      );
      blogToUpdate.likes -= 1;
    } else {
      // User has not liked the blog, so add like
      blogToUpdate.liked_by.push(userId);
      blogToUpdate.likes += 1;
    }

    // Update likes in API
    fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        likes: blogToUpdate.likes,
        liked_by: blogToUpdate.liked_by,
      }),
    })
      .then((response) => response.json())
      .then((data) => {
        setBlogs((prevBlogs) =>
          prevBlogs.map((blog) => (blog.id === data.id ? data : blog))
        );
      })
      .catch((error) => console.log(error));
  };

  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <span>{blog.likes}</span>
          <IconButton onClick={() => handleLikeClick(blog.id)}>
            {blog.liked_by.includes(userId) ? (
              <FavoriteIcon color="error" />
            ) : (
              <FavoriteIcon />
            )}
          </IconButton>
          <IconButton>
            <ShareIcon />
          </IconButton>
        </div>
      ))}
    </div>
  );
}

export default Likes;
