import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Login from './login';
import {  Navigate } from 'react-router-dom';

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright Â© '}
      <Link color="inherit" href="https://mui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const theme = createTheme();

function Signup() {
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [passwordValidation, setPasswordValidation] = useState("");
    const [emailValidation, setEmailValidation] = useState("");
    const [confirmPwdMsg, setConfirmPwdMsg] = useState("");
    const [register, setRegister] = useState(false);
    const [message, setMessage] = useState("");
    const [apiError, setApiError] = useState("");
    const [userErrorMessage,setUserErrorMessage] = useState("");
    const [usernameExistsError, setUsernameExistsError] = useState(null);
    const [errorMsg, setErrorMsg] = useState("");
    const [errorMessage, setErrorMessage] = useState('');
    const [emailExist,setEmailExist] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        // added check for empty input fields
        if(!username || !password || !email || !confirmPassword) {setErrorMsg("Please Fill the Input!")}


        const fetchdata = fetch("http://localhost:8000/api/register/", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username, email, password }),
            }).then((response) => {
                response.json().then((data) => {
                    console.log(data);
                    if (data.username) {
                      console.log(data.username);
                      setErrorMessage(data.username);
                    } else if (data.email) {
                      console.log(data.email);
                      setEmailExist(data.email);
                    }
                  }).catch(error => {
                    console.error(error);
            });

                if (response.status == 201 || response.ok) {
                    setRegister(true);
                    setMessage("Successfully signed up! Please wait while you are redirected to the login page...");
                    setTimeout(() => {
                        navigate("/login");
                    }, 1500);
                } else if (response.status === 409) {
                  // setUserErrorMessage(response.message);
                  setErrorMessage("USERNAME EXISTS");
                  throw new Error('Username is already taken');
                } else if(response.status == 400){
                    // setApiError("Bad request! An error occurred while signing up. Please try again.");
                }
                else {
                  // console.log(response.JSON()); 
                  setApiError("Something went wrong!");
                }
            })

            .catch((error) => {
                console.error(error);
                console.log(error.message);


            });
    };

    const handleUsernameChange = (event) => {

        setUsername(event.target.value);
    };

    const handleEmailChange = (event) => {
      const { value } = event.target;
      setEmail(value);

      // Email validation using regular expression
      const emailRegex = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;
      (!emailRegex.test(value)) ? setEmailValidation('Please enter a valid email address.') :  setEmailValidation('');
      setEmail(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
        const passwordRegex = /^(?=.*\d)(?=.*[!@#$%^&*])(?=.*[a-z])(?=.*[A-Z]).{8,}$/;
        event.target.value.length <= 8 && !passwordRegex.test(password) ? setPasswordValidation("Password must contain at least 8 characters including 1 uppercase, 1 lowercase, 1 digit and 1 special character") : setPasswordValidation("");
    };

    const handleConfirmPasswordChange = (event) => {
        setConfirmPassword(event.target.value);
        event.target.value !== password ? setConfirmPwdMsg("Passwords do not match") : setConfirmPwdMsg("");
    };

    return (
        <div>

<ThemeProvider theme={theme}>
       <Container component="main" maxWidth="xs">
         <CssBaseline />
         <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            Sign up
          </Typography>
          <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={12}>
                <TextField
                  autoComplete="given-name"
                  name="firstName"
                  required
                  fullWidth
                  id="firstName"
                  label="Username"
                  autoFocus
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                />
              </Grid>

            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
            >
              Sign Up
            </Button>
            <Grid container justifyContent="flex-end">
              <Grid item>
                <Link to="/login" variant="body2">
                  Already have an account? Sign in
                </Link>
              </Grid>
            </Grid>
          </Box>
        </Box>
        <Copyright sx={{ mt: 5 }} />
      </Container>
    </ThemeProvider>

            {register ? (
                <div>
                    <h4>{message}</h4>
                </div>
            ) : (
                <form onSubmit={handleSubmit} style={{ }}>
                    <div>                   
                        {/* <label htmlFor="username">Username:</label> */}
                        <div className="validationError">
                        {/* {userErrorMessage} {usernameExistsError && <p>{usernameExistsError}</p>}  */}
                        {/* {errorMessage && <div className="validationError">{errorMessage}</div>}  */}
                        {errorMsg}</div>
                        <input type="text" id="username" value={username} onChange={handleUsernameChange} placeholder="Enter Username"/>
                    </div>
                    <div>

                        {/* <label htmlFor="email">Email:</label> */}
                        <div className="validationError">{emailValidation} {errorMsg} {emailExist}  </div>
                        <input type="email" id="email" value={email} onChange={handleEmailChange} placeholder="Enter Email"/>
                    </div>
                    <div>
                        {/* <label htmlFor="password">Password:</label> */}
                        <div className="validationError">{passwordValidation && <span>{passwordValidation}</span>} {errorMsg} </div>
                        <input type="password" id="password" value={password} onChange={handlePasswordChange} placeholder="Enter Password"/>
                    </div>
                    <div>
                        {/* <label htmlFor="password">Confirm Password:</label> */}
                        <div className="validationError">{confirmPwdMsg && <span>{confirmPwdMsg}</span>} {errorMsg} </div>
                        <input type="password" id="confirmPassword" value={confirmPassword} onChange={handleConfirmPasswordChange} placeholder="Enter Confirm Password"/>
                    </div>
                    <br></br>

                    <div className="validationError">{apiError && <h4>{apiError}</h4>}</div>

                    <div>
                        <button type="submit">Signup</button>
                        <Link to="/login"> <a>Already Have an Account? Login </a></Link>
                    </div>
                </form>
            )}

        </div>
    );
}

