import { useState, useEffect } from "react";
import { IconButton } from "@mui/material";
import FavoriteIcon from '@mui/icons-material/Favorite';

export default function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);
  const [authorId, setAuthorId] = useState(0);
  const [countLikes, setCountLikes] = useState(0);
  const [liked, setLiked] = useState(0);
  const [likedBy, setLikedBy] = useState(0);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });

        const data = await response.json();

        // Initialize liked state for each blog post
        const blogsWithLiked = data.map((blog) => ({ ...blog, liked: false }));

        setBlogs(blogsWithLiked);
        setLikedBy(blogsWithLiked.liked_by);
        setCountLikes(blogsWithLiked);
        setAuthorId(blogsWithLiked.author);
        setLiked(blogsWithLiked.liked);
      } catch (error) {
        console.error(error);
        setError("Failed to fetch blog posts.");
      }
    };

    fetchBlogs();
  }, []);

  const handleLikeClick = async (blogId) => {
    try {
      const blogToUpdate = blogs.find((blog) => blog.id === blogId);
      let newLikes = blogToUpdate.likes;
      let newLikedByState = [...blogToUpdate.liked_by];
      let newLikedState = !blogToUpdate.liked;

      // check if the user has already liked this post

        if (newLikedState && newLikedByState.includes(authorId)) {
          newLikes += 1;
          newLikedByState = newLikedByState.filter((id) => id !== authorId);
          newLikedState = false;
        } 
        // check if the user has not liked this post yet
        else if (newLikedState && !newLikedByState.includes(authorId)) {
          newLikes += 1;
          newLikedByState.push(authorId);
        } 
        // check if the user wants to undo their like
        else if (!newLikedState && newLikedByState.includes(authorId)) {
          newLikes += 1;
          newLikedByState = newLikedByState.filter((id) => id !== authorId);
        }



      const updatedBlogs = blogs.map((blog) =>
        blog.id === blogId
          ? { ...blog, liked: newLikedState, likes: newLikes, liked_by: newLikedByState }
          : blog
      );
      setBlogs(updatedBlogs);

      // Update likes count
      setCountLikes(newLikes);

      // Call API to update like state
      await fetch(`http://localhost:8000/api/blog/likes/update/${blogId}/`, {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          liked: newLikedState,
          likes: newLikes,
          liked_by: newLikedByState
        }),
      });
    } catch (error) {
      console.error(error);
      setError("Failed to update like state.");
    }
  };

  return (
    <div>
    {blogs.map((blog) => (
    <div key={blog.id}>
      {blog.title} <br></br> <h6>likes</h6>
      {blog.likes}
      {/* {blog.likes} <br></br>
      {blog.liked_by} */}

      {blog.liked_by.includes(blog.author) ? <div> author has liked  <br></br>
        <IconButton onClick={() => handleLikeClick(blog.id)}>

    {blog.liked !== blog.liked_by.includes(blog.author) ? (
    <FavoriteIcon style={{ color: "red" }} />
    ) : (
    <FavoriteIcon style={{ color: "blue" }} />
    )}
    </IconButton>

      </div> : <div> 
        <h4>author iddnot liked</h4>
      <IconButton onClick={() => handleLikeClick(blog.id)}>
    {blog.liked !== blog.liked_by.includes(blog.author) ? (
    <FavoriteIcon style={{ color: "red" }} />
    ) : (
    <FavoriteIcon style={{ color: "blue" }} />
    )}
    </IconButton>
    </div>}





    </div>
    ))}
    </div>
    );
    }
