import { useState, useEffect } from 'react';

export default function CommentDetail({ authToken, blogId, commentId }) {
  const [commentData, setCommentData] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8000/api/blog/1/comments/${commentId}/`, {
      method: 'GET',
      headers: {
        'Authorization': `Token ${authToken}`,
        'Content-Type': 'application/json'
      }
    })
      .then(response => response.json())
      .then(data => setCommentData(data))
      .catch(error => console.error(error));
  }, [authToken, blogId, commentId]);

  if (!commentData) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h2>{commentData.comment}</h2>
      <p>Author: {commentData.user}</p>
      <p>Created at: {commentData.created_at}</p>
      <p>Likes: {commentData.likes}</p>
    </div>
  );
}
