import React, { useState, useEffect } from 'react'

import UpdateBlog from "./components/blogapp/update";

function Blog() {
  const [blogs, setBlogs] = useState([])
  const [loading, setLoading] = useState(true)
  const [newBlogTitle, setNewBlogTitle] = useState('')
  const [newBlogContent, setNewBlogContent] = useState('')
  const [newBlogAuthorName, setNewBlogAuthorName] = useState('')
  const [selectedBlogId, setSelectedBlogId] = useState('')


  useEffect(() => {
    fetch('http://localhost:8000/api/blog/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      setBlogs(data)
      setLoading(false)
    })
    .catch(error => console.error(error))
  }, [])

  const handleCreate = () => {
    const newBlog = {
      title: newBlogTitle,
      content: newBlogContent,
      authorName: newBlogAuthorName
    }

    fetch('http://localhost:8000/api/blog/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(newBlog)
    })
    .then(response => response.json())
    .then(data => {
      setBlogs([...blogs, data])
      setNewBlogTitle('')
      setNewBlogContent('')
      setNewBlogAuthorName('')
    })
    .catch(error => console.error(error))
  }

//   const handleUpdate = () => {
//     const updatedBlog = {
//       id: selectedBlogId,
//       title: updatedBlogTitle,
//       content: updatedBlogContent,
//       authorName: updatedBlogAuthorName
//     }

//     fetch(`http://localhost:8000/api/blog/${updatedBlog.id}/`, {
//       method: 'PUT',
