import React, { useState, useEffect } from 'react'

function Blog() {
  const [blogs, setBlogs] = useState([])
  const [loading, setLoading] = useState(true)
  const [newBlogTitle, setNewBlogTitle] = useState('')
  const [newBlogContent, setNewBlogContent] = useState('')
  const [newBlogAuthorName, setNewBlogAuthorName] = useState('')
  const [selectedBlogId, setSelectedBlogId] = useState('')
  const [updatedBlogTitle, setUpdatedBlogTitle] = useState('')
  const [updatedBlogContent, setUpdatedBlogContent] = useState('')
  const [updatedBlogAuthorName, setUpdatedBlogAuthorName] = useState('')

  useEffect(() => {
    fetch('http://localhost:8000/api/blog/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      setBlogs(data)
      setLoading(false)
    })
    .catch(error => console.error(error))
  }, [])

  const handleCreate = () => {
    const newBlog = {
      title: newBlogTitle,
      content: newBlogContent,
      authorName: newBlogAuthorName
    }

    fetch('http://localhost:8000/api/blog/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(newBlog)
    })
    .then(response => response.json())
    .then(data => {
      setBlogs([...blogs, data])
      setNewBlogTitle('')
      setNewBlogContent('')
      setNewBlogAuthorName('')
    })
    .catch(error => console.error(error))
  }

  const handleUpdate = () => {
    const updatedBlog = {
      id: selectedBlogId,
      title: updatedBlogTitle,
      content: updatedBlogContent,
      authorName: updatedBlogAuthorName
    }

    fetch(`http://localhost:8000/api/blog/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(updatedBlog)
    })
    .then(response => response.json())
    .then(data => {
      const index = blogs.findIndex(blog => blog.id === data.id)
      const newBlogs = [...blogs]
      newBlogs[index] = data
      setBlogs(newBlogs)
      setSelectedBlogId('')
      setUpdatedBlogTitle('')
      setUpdatedBlogContent('')
      setUpdatedBlogAuthorName('')
    })
    .catch(error => console.error(error))
  }

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      fetch(`http://localhost:8000/api/blog/${id}/`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        }
      })
      .then(response => {
        if (response.ok) {
          setBlogs(blogs.filter(blog => blog.id !== id))
        } else {
          throw new Error('Delete failed')
        }
      })
      .catch(error => {
        console.error(error)
      })
    }
  }

  if (loading) {
    return <p>Loading...</p>
  }
  return (
    <div>

    <div>
    <h2>Create New Blog</h2>
    <label>
    Title:
    <input
    type="text"
    value={newBlogTitle}
    onChange={(event) => setNewBlogTitle(event.target.value)}
    />
    </label>
    <br />
    <label>
    Content:
    <textarea
    value={newBlogContent}
    onChange={(event) => setNewBlogContent(event.target.value)}
    />
    </label>
    <br />
    <label>
    Author Name:
    <input
    type="text"
    value={newBlogAuthorName}
    onChange={(event) => setNewBlogAuthorName(event.target.value)}
    />
    </label>
    <br />
    <button onClick={handleCreate}>Create Blog</button>
    </div>
    </div>
    )
    }
export default Blog;
