import { useState, useEffect } from "react";

function Likes() {
  const [blogs, setBlogs] = useState([]);
  const [authorId, setAuthorId] = useState(0)

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:8000/api/blog/list/", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
          },
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        console.log(data); // log the data to the console
        setBlogs(data); // update the state with the fetched data
        setAuthorId(data.author_id);
      } catch (error) {
        console.error("Error fetching blogs:", error);
      }
    };

    fetchBlogs();
  }, []);

  const handleLike = async (authorId) => {
    try {
      const response = await fetch(
        `http://localhost:8000/api/blog/likes/update/${authorId}/`,
        {
          method: "PUT",
          headers: {
            Authorization: `Bearer ${localStorage.getItem("accessToken")}`,
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            likes: 1,
          }),
        }
      );
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      const updatedBlogs = blogs.map((blog) => {
        if (blog.id === authorId) {
          return {
            ...blog,
            likes: data.likes,
            liked_by: [...blog.liked_by, data.user_id],
          };
        }
        return blog;
      });
      setBlogs(updatedBlogs);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog.id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          <p>
            {blog.likes} {blog.likes === 1 ? "like" : "likes"}
          </p>
          <p>
            Liked by{" "}
            {blog.liked_by.length > 0
              ? blog.liked_by.join(", ")
              : "nobody yet"}
          </p>
          <button onClick={() => handleLike(blog.id)}>Like</button>
        </div>
      ))}
    </div>
  );
}

export default Likes;
