import React, { useState, useEffect } from 'react'
import UpdateBlog from "./components/blogapp/update";
import moment from 'moment-timezone';


function Blog() {

  const [loading, setLoading] = useState(true)
  const [newBlogTitle, setNewBlogTitle] = useState('')
  const [newBlogContent, setNewBlogContent] = useState('')
  const [newBlogAuthorName, setNewBlogAuthorName] = useState('')


  const [blogs, setBlogs] = useState([]);
  const [selectedBlogId, setSelectedBlogId] = useState('');
  const [updatedBlogTitle, setUpdatedBlogTitle] = useState(undefined);
  const [updatedBlogContent, setUpdatedBlogContent] = useState(undefined);
  const [updatedBlogAuthorName, setUpdatedBlogAuthorName] = useState(undefined);

  const [created_at, setCreatedAt] = useState("");
  const [localTime, setLocalTime] = useState(null);


  const date = created_at.substring(0, 10);
  const time = new Date(created_at).toLocaleTimeString();
  console.log("localtime", time); // prints the local time

  const timezone = moment.tz.guess();
  console.log("timezone", timezone);

  useEffect(() => {
    fetch('http://localhost:8000/api/blog/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      }
    })
    .then(response => response.json())
    .then(data => {
      setBlogs(data)
      setLoading(false)
      console.log(data[0].created_at);
      setCreatedAt(data[0].created_at);
    })
    .catch(error => console.error(error))
  }, [])

  const handleCreate = () => {
    const newBlog = {
      title: newBlogTitle,
      content: newBlogContent,
      authorName: newBlogAuthorName
    }

    fetch('http://localhost:8000/api/blog/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(newBlog)
    })
    .then(response => response.json())
    .then(data => {
      setBlogs([...blogs, data])
      setNewBlogTitle('')
      setNewBlogContent('')
      setNewBlogAuthorName('')
    })
    .catch(error => console.error(error))
  }


  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      fetch(`http://localhost:8000/api/blog/${id}/`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
        }
      })
      .then(response => {
        if (response.ok) {
          setBlogs(blogs.filter(blog => blog.id !== id))
        } else {
          throw new Error('Delete failed')
        }
      })
      .catch(error => {
        console.error(error)
      })
    }
  }

  const handleUpdate = () => {
    const updatedBlog = {
      id: selectedBlogId,
      title: updatedBlogTitle === undefined ? blogs.find(blog => blog.id === selectedBlogId).title : updatedBlogTitle,
      content: updatedBlogContent === undefined ? blogs.find(blog => blog.id === selectedBlogId).content : updatedBlogContent,
      authorName: updatedBlogAuthorName === undefined ? blogs.find(blog => blog.id === selectedBlogId).authorName : updatedBlogAuthorName
    }

    fetch(`http://localhost:8000/api/blog/${updatedBlog.id}/`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
      },
      body: JSON.stringify(updatedBlog)
    })
    .then(response => response.json())
    .then(data => {
      const index = blogs.findIndex(blog => blog.id === data.id)
      const newBlogs = [...blogs]
      newBlogs[index] = data
      setBlogs(newBlogs)
      setSelectedBlogId('')
      setUpdatedBlogTitle(undefined)
      setUpdatedBlogContent(undefined)
      setUpdatedBlogAuthorName(undefined)
    })
    .catch(error => console.error(error))
  }

  if (loading) {
    return <p>Loading...</p>
  }

  const sortedBlogs = blogs.sort((a, b) => b.id - a.id)
  return (
    <div>
<ul>
<div>
    <h2>Create New Blog</h2>
    <label>
    Title:
    <input
    type="text"
    value={newBlogTitle}
    onChange={(event) => setNewBlogTitle(event.target.value)}
    />
    </label>
    <br />
    <label>
    Content:
    <textarea
    value={newBlogContent}
    onChange={(event) => setNewBlogContent(event.target.value)}
    />
    </label>
    <br />
    <label>
    Author Name:
    <input
    type="text"
    value={newBlogAuthorName}
    onChange={(event) => setNewBlogAuthorName(event.target.value)}
    />
    </label>
    <br />
    <button onClick={handleCreate}>Create Blog</button>
    </div>


    </ul>
    {/* <UpdateBlog/> */}
    {selectedBlogId !== '' && (
        <div>

          <h2>Update Blog "{blogs.find(blog => blog.id === selectedBlogId).title}"</h2>

          <div>
            <label htmlFor="blog-title">Title:</label>
            <input type="text" id="blog-title" value={updatedBlogTitle === undefined ? blogs.find(blog => blog.id === selectedBlogId).title : updatedBlogTitle} onChange={e => setUpdatedBlogTitle(e.target.value)} />
          </div>

          <div>
            <label htmlFor="blog-content">Content:</label>
            <textarea id="blog-content" value={updatedBlogContent === undefined ? blogs.find(blog => blog.id === selectedBlogId).content : updatedBlogContent} onChange={e => setUpdatedBlogContent(e.target.value)} />
          </div>

          <div>
            <label htmlFor="blog-author-name">Author Name:</label>
            <input type="text" id="blog-author-name" value={updatedBlogAuthorName === undefined ? blogs.find(blog => blog.id === selectedBlogId).authorName : updatedBlogAuthorName} onChange={e => setUpdatedBlogAuthorName(e.target.value)} />
            </div>

            <button onClick={handleUpdate}>Update Blog</button>

            </div>
            )}



<h1>Blog List</h1>

    {sortedBlogs.map(blog => (
      <li key={blog.id}>
        <h2>{blog.title}</h2>
        <p>{blog.content}</p>
        <p>Author: {blog.authorName}</p>
        <p>Published Date: {blog.created_at}</p>
        <p>Updated Date: {blog.updated_at}</p>
        {created_at && <p>Blog Created at Standard: {created_at}</p>}
        <p>Date: {date}</p>  <p>Local-Time: {time}</p>
        <button onClick={() => setSelectedBlogId(blog.id)}>Edit</button>
        <button onClick={() => handleDelete(blog.id)}>Delete</button>
      </li>
    ))}

    </div>
    )
    }
export default Blog;
